import React, { useEffect, useState } from 'react';
import suitmediaLogo from '../../assets/suitmedialogo.png';
import '../navbar/Navbar.css'

const Navbar = ()=> {
    const [scrollingUp, setScrollingUp] = useState(true);
    const [activeSection, setActiveSection] = useState(''); // Add state to track active section

    useEffect(() => {
        const handleScroll = () => {
            const scrollTop = window.scrollY;

            // Determine scrolling direction
            setScrollingUp(scrollTop < (window.scrollY || document.documentElement.scrollTop));

            // Detect active section based on scroll position
            const sections = document.querySelectorAll('section');
            sections.forEach((section) => {
                const sectionTop = section.offsetTop - 50; // Adjust this value based on your layout
                const sectionBottom = sectionTop + section.offsetHeight;
                if (scrollTop >= sectionTop && scrollTop < sectionBottom) {
                    setActiveSection(section.id);
                }
            });
        };

        window.addEventListener('scroll', handleScroll);
        return () => {
            window.removeEventListener('scroll', handleScroll);
        };
    }, []);

    return (
        <div>
            <nav className={`navbar navbar-expand-lg fixed-top ${!scrollingUp ? 'hidden' : ''}`}>
                <div className="container-fluid">
                    <a className="navbar-brand" href="/#">
                        <img src={suitmediaLogo} alt='' width={125}></img>
                    </a>
                    <div className="collapse navbar-collapse">
                        <ul className="navbar-nav ms-auto">
                            <li className="nav-item">
                                <a className="nav-link" href="/#">Work</a>
                            </li>
                            <li className="nav-item">
                                <a className="nav-link" href="/#">About</a>
                            </li>
                            <li className="nav-item">
                                <a className="nav-link" href="/#">Services</a>
                            </li>
                            <li className="nav-item">
                                <a className="nav-link" href="/#">Ideas</a>
                            </li>
                            <li className="nav-item">
                                <a className="nav-link" href="/#">Careers</a>
                            </li>
                            <li className="nav-item">
                                <a className="nav-link" href="/#">Contact</a>
                            </li>
                        </ul>
                    </div>
                </div>
            </nav>
        </div>
    )
}

export default Navbar;
