import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Navbar from '../../components/navbar/Navbar';
import BannerImg from '../../assets/banner.jpg';
import './Ideas.css'

const Ideas = ()=> {
    const [data, setData] = useState([]);
    const [perPage, setPerPage] = useState(10); // Default value for perPage
    const [sortBy, setSortBy] = useState('published_at'); // Default value for sortBy
    const [currentPage, setCurrentPage] = useState(1); // Default value for currentPage

    useEffect(() => {
        const fetchData = async () => {
          try {

            const response = await axios.get(
              `https://suitmedia-backend.suitdev.com/api/ideas?page[number]=${currentPage}&page[size]=${perPage}&append[]=small_image&append[]=medium_image&sort=-${sortBy}`
            );

            setData(response.data);
            console.log('Data from API:', response.data);
          } catch (error) {
            console.error("Error: ", error);
          }
        };

        fetchData();
      }, [perPage, sortBy, currentPage]);

    const BannerStyle = {
        backgroundImage: `url(${BannerImg})`,
        backgroundSize: 'cover',
        clipPath: 'polygon(0 0, 100% 0, 100% 70%, 0 100%)'
    };

    return (
        <div>
            <Navbar />
            <section id='hero' style={BannerStyle}>
                <div className='container-fluid d-flex justify-content-center align-items-center' style={{ minHeight : '100vh' }}>
                    <div className='content-hero'>
                        <h1>Ideas</h1>
                        <p>Where all our great things begin</p>
                    </div>
                </div>
            </section>

            <section id='content-card'>
                <div className='container-fluid mt-5'>
                    <div className='row'>
                        <div className='col-6 d-flex justify-content-start'>
                            <h5 className='mt-2'>Showing 1 - 10 of 100</h5>
                        </div>
                        <div className='col-6 d-flex justify-content-end'>
                            <h5 className='mt-2'>Show per page : </h5>
                            <select className="form-select ms-3">
                                <option value="10">10</option>
                                <option value="20">20</option>
                                <option value="30">50</option>
                            </select>
                            <h5 className='mt-2 ms-5'>Sort by : </h5>
                            <select className="form-select ms-3">
                                <option value="10">Newest</option>
                                <option value="20">Oldest</option>
                            </select>
                        </div>
                    </div>
                </div>
            </section>
        </div>
    )
}

export default Ideas;
