import React, { useState, useEffect } from 'react';
import Navbar from '../../components/navbar/Navbar';
import Card from '../../components/card/Card'
import BannerImg from '../../assets/banner.jpg';
import { ApiConsume } from '../../utility/Api';
import './Ideas.css'

const Ideas = ()=> {
    const [ideasData, setIdeasData] = useState([]);
    const [perPage, setPerPage] = useState(10);
    const [sortBy, setSortBy] = useState('published_at');
    const [pageNumber, setPageNumber] = useState(1);

    const BannerStyle = {
        backgroundImage: `url(${BannerImg})`,
        backgroundSize: 'cover',
        clipPath: 'polygon(0 0, 100% 0, 100% 70%, 0 100%)'
    };

    useEffect(() => {
        const fetchData = async () => {
          try {
            const data = await ApiConsume({
              pageNumber: pageNumber,
              pageSize: perPage, 
              sorting: sortBy 
            });
            setIdeasData(data);
            console.log('Fetched Data:', data);
            console.log('Banyak Data:', data.length);
          } catch (error) {
            console.error('Error fetching ideas:', error); 
          }
        };

        fetchData();
    }, [pageNumber, perPage, sortBy])  

    return (
        <div>
            <Navbar />
            <section id='hero' style={BannerStyle}>
                <div className='container-fluid d-flex justify-content-center align-items-center' style={{ minHeight : '100vh' }}>
                    <div className='content-hero'>
                        <h1>Ideas</h1>
                        <p>Where all our great things begin</p>
                    </div>
                </div>
            </section>

            <section id='content-card'>
                <div className='container-fluid mt-5'>
                    <div className='row mb-5'>
                        <div className='col-6 d-flex justify-content-start'>
                            <h5 className='mt-2'>Showing 1 - {perPage} of 100</h5>
                        </div>
                        <div className='col-6 d-flex justify-content-end'>
                            <h5 className='mt-2'>Show per page : </h5>
                            <select className="form-select ms-3">
                                <option value="10">10</option>
                                <option value="20">20</option>
                                <option value="50">50</option>
                            </select>
                            <h5 className='mt-2 ms-5'>Sort by : </h5>
                            <select className="form-select ms-3">
                                <option value="newest">Newest</option>
                                <option value="oldest">Oldest</option>
                            </select>
                        </div>
                    </div>
                    <div className='row'>
                    {ideasData.map(data => (
                        <Card
                        key={data.id}
                        imageUrl={data.medium_image[0]?.url}
                        dateCreated={data.created_at}
                        title={data.title}
                        />
                    ))}
                    </div>
                </div>
            </section>
        </div>
    )
}

export default Ideas;
